import React, { useState } from 'react';
import './Donation.css';
import axios from 'axios';

function Donation({ customerId }) {
  // State variables to store donation information
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [amount, setAmount] = useState(0);
  const [donationDate, setDonationDate] = useState('');
  const [showSpecialInput, setShowSpecialInput] = useState(false);
  const [specialName, setSpecialName] = useState('');

  // Function to handle the main form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    const currentDate = new Date();
    const formattedDate = `${currentDate.getFullYear()}-${(currentDate.getMonth() + 1).toString().padStart(2, '0')}-${currentDate.getDate().toString().padStart(2, '0')}`;
    setDonationDate(prevDate => formattedDate); // Update donation date state

    console.log('Donor Name:', name);
    console.log('Donor Email:', email);
    console.log('Donation Amount (USD):', amount);
    console.log('Donation Date:', formattedDate);

    if (amount >= 100) {
      setShowSpecialInput(true); // Show the special input field if the donation is $100 or more
    }

    // Prepare data for the API request
    const data = {
      donationAmount: amount,
      customerId: customerId,
      donationDate: formattedDate
    };

    // API call to submit donation data
    axios.post('http://localhost:5095/api/ZooDb/NewDonation', data)
      .then((res) => { console.log(res); });
  };

  // Function to handle special name submission
  const handleSpecialNameSubmit = (event) => {
    event.preventDefault();

    // Prepare special name data
    const data = {
      customerId: customerId,
      specialName: specialName
    };

    // API call to submit special name, replace the URL with your actual endpoint
    axios.post('', data)
      .then((res) => { console.log(res); });
  };

  return (
    <div className="App-donation">
      <main className="Main-donation">
        <p>To make a donation, please fill out the form below:</p>
        <form className="DonationForm" onSubmit={handleSubmit}>
          <label htmlFor="amount">Amount (USD):</label>
          <input type="number" id="amount" name="amount" min="1" required value={amount} onChange={(e) => setAmount(parseFloat(e.target.value))} />
          <button type="submit">Donate</button>
        </form>
        {showSpecialInput && (
          <form onSubmit={handleSpecialNameSubmit}>
            <label htmlFor="specialName">Special Name for a new animal (optional):</label>
            <input type="text" id="specialName" name="specialName" value={specialName} onChange={(e) => setSpecialName(e.target.value)} />
            <p>This name may be selected for a name for a new animal.</p>
            <button type="submit">Submit Name</button>
          </form>
        )}
      </main>
    </div>
  );
};

export default Donation;
