import React, { useState } from 'react';
import './SecurityReport.css';

function SecurityReport({ securityReports }) {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredReports, setFilteredReports] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Filter security reports based on the selected date range
    const filtered = securityReports.filter(report => {
      return report.date >= startDate && report.date <= endDate;
    });
    setFilteredReports(filtered);
  };

  return (
    <div className="security-report-container">
      <h2>Security Report Search</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="startDate">Start Date:</label>
          <input
            type="date"
            id="startDate"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="endDate">End Date:</label>
          <input
            type="date"
            id="endDate"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            required
          />
        </div>
        <button type="submit">Search</button>
      </form>
      {filteredReports.length > 0 && (
        <div className="report-list">
          <h3>Security Reports:</h3>
          <ul>
            {filteredReports.map((report, index) => (
              <li key={index}>
                <p>Date: {report.date}</p>
                <p>Time: {report.time}</p>
                <p>Description: {report.description}</p>
                <p>Location: {report.location}</p>
                <p>Severity Level: {report.severityLevel}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default SecurityReport;
