import React, { useState, useEffect } from 'react';
import './AddAnimal.css';
import axios from 'axios';

function AddAnimal() {
  const [animalName, setAnimalName] = useState('');
  const [animalSpecies, setAnimalSpecies] = useState('');
  const [animalGender, setAnimalGender] = useState('');
  const [animalDoB, setAnimalDoB] = useState('');
  const [animalEndangered, setAnimalEndangered] = useState(false);
  const [animalOrigin, setAnimalOrigin] = useState('');
  const [animalDoA, setAnimalDoA] = useState('');
  const [donatedNames, setDonatedNames] = useState([]);

  useEffect(() => {
    fetchDonatedNames();
  }, []);

  const fetchDonatedNames = async () => {
    try {
      const response = await axios.get('http://localhost:5095/api/ZooDb/GetDonatedNames');
      setDonatedNames(response.data); // assuming the API returns an array of names
    } catch (error) {
      console.error('Failed to fetch donated names:', error);
    }
  };

  const handleDeleteDonatedName = async (name) => {
    try {
      await axios.delete(`http://localhost:5095/api/ZooDb/DeleteDonatedName?donatedName=${name}`);
      fetchDonatedNames(); // Refresh the list after deletion
      alert(`Deleted: ${name}`);
    } catch (error) {
      alert('Failed to delete name. Please try again.');
      console.error('Error deleting donated name:', error);
    }
  };

  const handleReset = () => {
    setAnimalName('');
    setAnimalSpecies('');
    setAnimalGender('');
    setAnimalDoB('');
    setAnimalEndangered(false);
    setAnimalOrigin('');
    setAnimalDoA('');
  };

  async function handleSubmit(event) {
    event.preventDefault();
    // Other form submission logic here
  };

  return (
    <div className="add-animal-main-container">
      <div className="donated-names-sidebar">
        <h3>Donated Names</h3>
        <ul>
          {donatedNames.map((name, index) => (
            <li key={index}>
              {name}
              <button onClick={() => handleDeleteDonatedName(name)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="add-animal-container">
        <h2>Add Animal</h2>
        {/* Rest of your form here */}
      </div>
    </div>
  );
}

export default AddAnimal;
