import React, { useState } from 'react';
import './ViewMemberships.css';

function ViewMemberships({ customerId }) {
  const [selectedMembership, setSelectedMembership] = useState(null);
  const memberships = [
    {
      id: 1,
      name: 'Individual',
      fee: '$50/year',
      benefits: [
        "15% off on ticket purchases",
        "1 free guest pass per year"
      ]
    },
    {
      id: 2,
      name: 'Couple',
      fee: '$90/year',
      benefits: [
        "20% off on ticket purchases",
        "2 free guest passes per year"
      ]
    },
    {
      id: 3,
      name: 'Family',
      fee: '$150/year',
      benefits: [
        "25% off on ticket purchases",
        "4 free guest passes per year",
      ]
    },
    {
      id: 4,
      name: 'Premium',
      fee: '$200/year',
      benefits: [
        "30% off on ticket purchases",
        "6 free guest passes per year",
      ]
    }
  ];

  const selectMembership = (membership) => {
    // Toggle membership selection
    if (selectedMembership && selectedMembership.id === membership.id) {
      setSelectedMembership(null); // Deselect if already selected
    } else {
      setSelectedMembership(membership); // Select new membership
    }
  };

  return (
    <div className="memberships-container">
      <h1 className="title">Zoo Memberships</h1>
      <div className="memberships-list">
        {memberships.map(membership => (
          <div key={membership.id} className="membership-card" onClick={() => selectMembership(membership)}
               style={{ transform: selectedMembership && selectedMembership.id === membership.id ? 'scale(1.15)' : 'scale(1)' }}>
            <h2>{membership.name}</h2>
            <p>{membership.fee}</p>
            <ul>
              {membership.benefits.map(benefit => (
                <li key={benefit}>{benefit}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
      {selectedMembership && (
        <div className="cart-display">
          <h2>Selected Membership:</h2>
          <p>{selectedMembership.name} - {selectedMembership.fee}</p>
          <ul>
            {selectedMembership.benefits.map(benefit => (
              <li key={benefit}>{benefit}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ViewMemberships;
