import React, { useState } from 'react';
import "./AddEnclosureForm.css"

const AddEnclosureForm = ({ onAddEnclosure }) => {
  const [enclosureData, setEnclosureData] = useState({
    enclosure_id: '',
    name: '',
    enclosure_type: '',
    build_date: '',
    address: '',
    cleaning_start: '',
    cleaning_end: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEnclosureData({ ...enclosureData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!enclosureData.name || !enclosureData.enclosure_id || !enclosureData.enclosure_type || !enclosureData.build_date || !enclosureData.address || !enclosureData.cleaning_start || !enclosureData.cleaning_end) {
      alert('Please fill in all fields');
      return;
    }
    onAddEnclosure(enclosureData);
    setEnclosureData({
      enclosure_id: '',
      name: '',
      enclosure_type: '',
      build_date: '',
      address: '',
      cleaning_start: '',
      cleaning_end: ''
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="label">Enclosure ID:</div>
      <input
        type="number"
        placeholder="Enclosure ID"
        name="enclosure_id"
        value={enclosureData.enclosure_id}
        onChange={handleChange}
      />
      <div className="label">Name:</div>
      <input
        type="text"
        placeholder="Name"
        name="name"
        value={enclosureData.name}
        onChange={handleChange}
      />
      <div className="label">Enclosure Type:</div>
      <input
        type="text"
        placeholder="Enclosure Type"
        name="enclosure_type"
        value={enclosureData.enclosure_type}
        onChange={handleChange}
      />
      <div className="label">Build Date:</div>
      <input
        type="date"
        placeholder="Build Date"
        name="build_date"
        value={enclosureData.build_date}
        onChange={handleChange}
      />
      <div className="label">Address:</div>
      <input
        type="text"
        placeholder="Address"
        name="address"
        value={enclosureData.address}
        onChange={handleChange}
      />
      <div className="label">Cleaning Start Time:</div>
      <input
        type="time"
        placeholder="Cleaning Start Time"
        name="cleaning_start"
        value={enclosureData.cleaning_start}
        onChange={handleChange}
      />
      <div className="label">Cleaning End Time:</div>
      <input
        type="time"
        placeholder="Cleaning End Time"
        name="cleaning_end"
        value={enclosureData.cleaning_end}
        onChange={handleChange}
      />

      <br></br>

      <button type="submit">Add Enclosure</button>
      <button type="button" onClick={() => setEnclosureData({
        enclosure_id: '',
        name: '',
        enclosure_type: '',
        build_date: '',
        address: '',
        cleaning_start: '',
        cleaning_end: ''
      })}>Reset</button>
    </form>
  );
};

export default AddEnclosureForm;
