import React, { useState } from 'react';
import "./VeterinarianRecords.css"

function VeterinarianRecord() {
  const [weight, setWeight] = useState('');
  const [height, setHeight] = useState('');
  const [medications, setMedications] = useState('');
  const [treatments, setTreatments] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validate weight and height
    if (isNaN(weight) || weight <= 0 || isNaN(height) || height <= 0) {
      alert('Please enter valid positive numbers for weight and height.');
      return;
    }

    // Split medications and treatments by comma and remove empty strings
    const medicationsArray = medications.split(',').filter((med) => med.trim() !== '');
    const treatmentsArray = treatments.split(',').filter((treat) => treat.trim() !== '');

    // Here you can perform any backend logic (e.g., send data to a server)
    console.log('Weight:', weight);
    console.log('Height:', height);
    console.log('Medications:', medicationsArray);
    console.log('Treatments:', treatmentsArray);

    setWeight('');
    setHeight('');
    setMedications('');
    setTreatments('');
  };

  return (
    <div className="veterinarian-records-container">
      <h2>Veterinarian Records</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="weight">Weight (kg):</label>
          <input
            type="number"
            id="weight"
            value={weight}
            onChange={(e) => setWeight(Math.max(0, parseFloat(e.target.value)))}
            min="0"
            step="0.01"
          />
        </div>
        <div className="form-group">
          <label htmlFor="height">Height (cm):</label>
          <input
            type="number"
            id="height"
            value={height}
            onChange={(e) => setHeight(Math.max(0, parseFloat(e.target.value)))}
            min="0"
            step="0.01"
          />
        </div>
        <div className="form-group">
          <label htmlFor="medications">Medications:</label>
          <textarea
            id="medications"
            value={medications}
            onChange={(e) => setMedications(e.target.value)}
            placeholder="Enter medications separated by commas"
          />
        </div>
        <div className="form-group">
          <label htmlFor="treatments">Treatments:</label>
          <textarea
            id="treatments"
            value={treatments}
            onChange={(e) => setTreatments(e.target.value)}
            placeholder="Enter treatments separated by commas"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default VeterinarianRecords;
