import React, { useState } from 'react';
import axios from 'axios';
import './ClockIn.css';

function ClockIn() {
  const [employeeId, setEmployeeId] = useState('');
  const [clockInTime, setClockInTime] = useState(null);
  const [clockOutTime, setClockOutTime] = useState(null);
  const [message, setMessage] = useState('');

  const handleClockIn = async () => {
    try {
      await axios.post('/clock-in', { employeeId });
      setClockInTime(new Date());
      setMessage('Clock-in successful');
    } catch (error) {
      console.error('Clock-in error:', error);
      setMessage('Clock-in failed');
    }
  };

  const handleClockOut = async () => {
    try {
      await axios.post('/clock-out', { employeeId });
      setClockOutTime(new Date());
      setMessage('Clock-out successful');

      // Calculate total hours worked and send to backend
      const totalHoursWorked = calculateHoursWorked();
      // Assuming there's an endpoint to send total hours worked to the backend
      await axios.post('/total-hours-worked', { employeeId, totalHoursWorked });
      console.log('Total hours worked sent to backend:', totalHoursWorked);

    } catch (error) {
      console.error('Clock-out error:', error);
      setMessage('Clock-out failed');
    }
  };

  const calculateHoursWorked = () => {
    if (clockInTime && clockOutTime) {
      const diff = Math.abs(clockOutTime - clockInTime);
      const hours = Math.floor(diff / (1000 * 60 * 60));
      const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
      return `${hours} hours and ${minutes} minutes`;
    }
    return 'N/A';
  };

  return (
    <div className='clock-in'>
      <h1>Employee Clock In/Out</h1>
      <div>
        <label>
          Employee ID:
          <input type="text" value={employeeId} onChange={(e) => setEmployeeId(e.target.value)} />
        </label>
      </div>
      <br></br>
      <div>
        <button onClick={handleClockIn}>Clock In</button>
        <button onClick={handleClockOut}>Clock Out</button>
      </div>
      <div>
        <p>Clock In Time: {clockInTime ? clockInTime.toLocaleString() : 'N/A'}</p>
        <p>Clock Out Time: {clockOutTime ? clockOutTime.toLocaleString() : 'N/A'}</p>
        <p>Total Hours Worked: {calculateHoursWorked()}</p>
      </div>
      {message && <p>{message}</p>}
    </div>
  );
}

export default ClockIn;
