import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './TicketBuy.css';
import axios from 'axios';

function TicketBuy({ customerId }) {
  const [adultTickets, setAdultTickets] = useState(0);
  const [childTickets, setChildTickets] = useState(0);
  const [seniorTickets, setSeniorTickets] = useState(0);
  const [infantTickets, setInfantTickets] = useState(0);
  const [selectedDate, setSelectedDate] = useState(null);
  const [discount, setDiscount] = useState(false);
  const [message, setMessage] = useState('');

  const adultPrice = 10;
  const childPrice = 7;
  const seniorPrice = 6;
  const infantPrice = 5;

  const handleAdultChange = (event) => {
    const value = Number(event.target.value);
    setAdultTickets(value >= 0 ? value : 0);
  };

  const handleChildChange = (event) => {
    const value = Number(event.target.value);
    setChildTickets(value >= 0 ? value : 0);
  };

  const handleSeniorChange = (event) => {
    const value = Number(event.target.value);
    setSeniorTickets(value >= 0 ? value : 0);
  };

  const handleInfantChange = (event) => {
    const value = Number(event.target.value);
    setInfantTickets(value >= 0 ? value : 0);
  };

  const totalCost =
    adultTickets * adultPrice +
    childTickets * childPrice +
    seniorTickets * seniorPrice +
    infantTickets * infantPrice;

  // Calculate discounted total cost
  const discountedTotalCost = discount ? totalCost * 0.9 : totalCost;

  const handleSubmit = (event) => {
    event.preventDefault();
    // Check if the date is selected
    if (!selectedDate) {
      alert('Please select a date.');
      return;
    }

    const userData = {
      CustomerId: customerId,
    };

    axios
      .post(`http://localhost:5095/api/ZooDb/CheckDiscountStatus`, userData)
      .then((response) => {
        if (response.data.DiscountApplied) {
          console.log(response);
          setDiscount(true);
          setMessage(response.data.Message);
        }
      })
      .catch((error) => console.error('Error:', error));

    console.log('Adult Tickets:', adultTickets);
    console.log('Child Tickets:', childTickets);
    console.log('Senior Tickets:', seniorTickets);
    console.log('Infant Tickets:', infantTickets);
    console.log(
      'Selected Date:',
      selectedDate.toLocaleDateString('en-US', {
        month: '2-digit',
        day: '2-digit',
        year: 'numeric',
      })
    );
    console.log('Total Cost:', totalCost);

    // Format the selected date
    const formattedDate = selectedDate
      ? `${('0' + (selectedDate.getMonth() + 1)).slice(-2)}/${(
          '0' + selectedDate.getDate()
        ).slice(-2)}/${selectedDate.getFullYear()}`
      : null;

    const data = {
      adultTickets: adultTickets,
      childTickets: childTickets,
      seniorTickets: seniorTickets,
      infantTickets: infantTickets,
      formattedDate: formattedDate, // Use the formatted date
      totalCost: totalCost,
      customerId: customerId,
    };
    axios
      .post('http://localhost:5095/api/ZooDb/NewTickets', data)
      .then((res) => {
        console.log(res);
        alert('Tickets successfully purchased!');
        if (discount == true) {
          console.log('You are getting a discount');
        }
      })
      .catch((error) => {
        console.error('Error purchasing tickets:', error);
        alert('Failed to purchase tickets. Please try again.');
      });
  };

  return (
    <div className="ticket-buy-container">
      <div className="buy-menu">
        <h1>Welcome to the Zoo!</h1>
        <div className="ticket-types">
          {/* Ticket types JSX */}
        </div>

        {/* Datepicker */}
        <div className="datepicker-container">
          <label>Select Date:</label>
          <DatePicker
            selected={selectedDate}
            onChange={(date) => setSelectedDate(date)}
            minDate={new Date()}
            dateFormat="MM/dd/yyyy"
          />
        </div>

        {/* Total Cost */}
        <div className="total-cost">
          Total Cost: ${totalCost.toFixed(2)}
          {discount && (
            <span>
              {' '}
              (Discounted Total Cost: ${discountedTotalCost.toFixed(2)})
            </span>
          )}
        </div>

        {/* Buy Tickets Button */}
        <button type="submit" onClick={handleSubmit}>
          Buy Tickets
        </button>
      </div>
    </div>
  );
}

export default TicketBuy;
