import { useState } from "react";
import { Link, useMatch, useResolvedPath } from "react-router-dom";
import "./CustomerNavbar.css";

export default function CustomerNavbar() {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <nav className="nav-rest">
      <Link to="/" className="site-title-rest">
        Zoo
      </Link>
      <ul className="nav-list-rest">
        <CustomLink
          to="/tickets"
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
          className="nav-item-with-submenu-rest"
        >
          Tickets
          {isHovered && (
            <div className="sub-menu-rest">
              <CustomLink to="/ticket-view">View Tickets</CustomLink>
              <CustomLink to="/ticket-buy">Buy Tickets</CustomLink>
            </div>
          )}
        </CustomLink>
        <CustomLink to="/donation">Donateee</CustomLink>
        <CustomLink to="/about-us">About us</CustomLink>
        <CustomLink to="/sign-in">Sign out</CustomLink>
      </ul>
    </nav>
  );
}

function CustomLink({ to, children, onMouseEnter, onMouseLeave, ...props }) {
  const resolvedPath = useResolvedPath(to);
  const isActive = useMatch({ path: resolvedPath.pathname, end: true });

  return (
    <li className={`nav-item ${isActive ? "active" : ""}`} onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}>
      <Link to={to} className="nav-link-rest" {...props}>
        {children}
      </Link>
    </li>
  );
}
