import React, { useState, useEffect } from 'react';
import axios from 'axios';

function DeleteDonatedNameForm() {
  const [donatedNames, setDonatedNames] = useState([]);
  const [selectedNames, setSelectedNames] = useState([]);

  useEffect(() => {
    // Fetch donated names when the component mounts
    const fetchDonatedNames = async () => {
      try {
        const response = await axios.get('http://localhost:5095/api/ZooDb/GetDonatedNames');
        setDonatedNames(response.data);
      } catch (error) {
        console.error('Failed to fetch donated names:', error);
      }
    };

    fetchDonatedNames();
  }, []);

  const handleDelete = async () => {
    try {
      await Promise.all(selectedNames.map(async (name) => {
        await axios.delete(`http://localhost:5095/api/ZooDb/DeleteDonatedName/${name}`);
      }));
      // Refetch donated names after deletion
      const response = await axios.get('http://localhost:5095/api/ZooDb/GetDonatedNames');
      setDonatedNames(response.data);
      setSelectedNames([]);
    } catch (error) {
      console.error('Failed to delete donated names:', error);
    }
  };

  const handleCheckboxChange = (name) => {
    const isSelected = selectedNames.includes(name);
    if (isSelected) {
      setSelectedNames(selectedNames.filter(selectedName => selectedName !== name));
    } else {
      setSelectedNames([...selectedNames, name]);
    }
  };

  return (
    <div className="delete-donated-name-container">
      <h3>Delete Donated Names</h3>
      <ul className="donated-names-list">
        {donatedNames.map((name, index) => (
          <li key={index}>
            <label>
              <input
                type="checkbox"
                checked={selectedNames.includes(name)}
                onChange={() => handleCheckboxChange(name)}
              />
              {name}
            </label>
          </li>
        ))}
      </ul>
      <button onClick={handleDelete} disabled={selectedNames.length === 0}>Delete</button>
    </div>
  );
}

export default DeleteDonatedNameForm;
