import React, { useState } from 'react';
import "./AddEnclosureForm.css"

const AddEnclosureForm = ({ onAddEnclosure }) => {
  const [enclosure_id, setID] = useState('');
  const [name, setName] = useState('');
  const [enclosure_type, setType] = useState('');
  const [build_date, setBuildDate] = useState('');
  const [address, setAddress] = useState('');
  const [cleaning_start, setCleaningStart] = useState('');
  const [cleaning_end, setCleaningEnd] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can implement backend logic to send the enclosure data to the server
    console.log('Enclosure ID:', enclosure_id); // Stored enclosure ID
    console.log('Name:', name); // Stored name
    console.log('Enclosure Type:', enclosure_type); // Stored enclosure type
    console.log('Build Date:', build_date); // Stored build date
    console.log('Address:', address); // Stored address
    console.log('Cleaning Start Time:', cleaning_start); // Stored cleaning start time
    console.log('Cleaning End Time:', cleaning_end); // Stored cleaning end time

    // Resetting the form fields
    setID('');
    setName('');
    setType('');
    setBuildDate('');
    setAddress('');
    setCleaningStart('');
    setCleaningEnd('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="label">Enclosure ID:</div>
      <input
        type="number"
        placeholder="Enclosure ID"
        value={enclosure_id}
        onChange={(e) => setID(e.target.value)}
        min="0" // Minimum value constraint to ensure non-negative numbers
        required // Required field
      />
      <div className="label">Name:</div>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required // Required field
      />
      <div className="label">Enclosure Type:</div>
      <input
        type="text"
        placeholder="Enclosure Type"
        value={enclosure_type}
        onChange={(e) => setType(e.target.value)}
        required // Required field
      />
      <div className="label">Build Date:</div>
      <input
        type="date"
        placeholder="Build Date"
        value={build_date}
        onChange={(e) => setBuildDate(e.target.value)}
        required // Required field
      />
      <div className="label">Address:</div>
      <input
        type="text"
        placeholder="Address"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        required // Required field
      />
      <div className="label">Cleaning Start Time:</div>
      <input
        type="time"
        placeholder="Cleaning Start Time"
        value={cleaning_start}
        onChange={(e) => setCleaningStart(e.target.value)}
        pattern="(0?[1-9]|1[0-2]):[0-5][0-9] [AP]M" // Time format constraint (HH:MM AM/PM)
        required // Required field
      />
      <div className="label">Cleaning End Time:</div>
      <input
        type="time"
        placeholder="Cleaning End Time"
        value={cleaning_end}
        onChange={(e) => setCleaningEnd(e.target.value)}
        pattern="(0?[1-9]|1[0-2]):[0-5][0-9] [AP]M" // Time format constraint (HH:MM AM/PM)
        required // Required field
      />

      <br></br>

      <button type="submit">Add Enclosure</button>
    </form>
  );
};

export default AddEnclosureForm;
