import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeProfileView.css'; // Reusing the same CSS for styling consistency

function EmployeeProfileView({ empId }) {
  const [employee, setEmployee] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!empId) {
      console.error("No empId provided");
      setError("No employee ID provided");
      setIsLoading(false);
      return;
    }

    setIsLoading(true);
    axios.get(`http://localhost:5095/api/ZooDb/GetEmployeeProfile?empId=${empId}`)
      .then(response => {
        setEmployee(response.data);
        setError(null);
      })
      .catch(err => {
        console.error('Error fetching employee data:', err);
        setError('Failed to load employee data');
      })
      .finally(() => {
        setIsLoading(false);
      });
  }, [empId]);

  if (isLoading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">Error loading profile: {error}</div>;
  }

  if (!employee) {
    return <div className="error">No employee data available.</div>;
  }

  return (
    <div className="profile-container">
      <h1 className="profile-header">Employee Profile</h1>
      <div className="profile-info">
        <p className="fade-in">ðŸ‘¤ <strong>First Name:</strong> {employee.firstName}</p>
        <p className="fade-in">ðŸ‘¤ <strong>Last Name:</strong> {employee.lastName}</p>
        <p className="fade-in">ðŸ“… <strong>Hire Date:</strong> {employee.hireDate ? new Date(employee.hireDate).toLocaleDateString() : 'N/A'}</p>
        <p className="fade-in">ðŸŽ‚ <strong>Date of Birth:</strong> {employee.dob ? new Date(employee.dob).toLocaleDateString() : 'N/A'}</p>
        <p className="fade-in">ðŸ’° <strong>Salary:</strong> {employee.salary.toLocaleString()}</p>
        <p className="fade-in">ðŸ“§ <strong>Email:</strong> {employee.email}</p>
      </div>
    </div>
  );
}

export default EmployeeProfileView;
