import React, { useState } from 'react';
import './ClockIn.css';

function ClockIn() {
  const [employeeId, setEmployeeId] = useState('');
  const [clockInTime, setClockInTime] = useState(null);
  const [clockOutTime, setClockOutTime] = useState(null);
  const [totalHoursWorked, setTotalHoursWorked] = useState(null); // Total hours worked saved in state
  const [message, setMessage] = useState('');

  const handleClockIn = () => {
    const currentTime = new Date();
    setClockInTime(currentTime);
    setMessage('Clock-in successful');
  };

  const handleClockOut = () => {
    const currentTime = new Date();
    setClockOutTime(currentTime);

    if (clockInTime) {
      const diff = Math.abs(currentTime - clockInTime);
      const hours = Math.floor(diff / (1000 * 60 * 60));
      const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
      const totalHours = hours + minutes / 60;
      setTotalHoursWorked(totalHours); // Total hours worked calculated and saved in state
      setMessage('Clock-out successful');

      // Send totalHoursWorked to backend here
      // Example:
      // sendHoursWorkedToBackend(totalHours);
    } else {
      setMessage('Please clock in before clocking out');
    }
  };

  // Function to send total hours worked to the backend
  const sendHoursWorkedToBackend = (hours) => {
    // Use axios or any other method to send data to the backend
    // Example:
    // axios.post('/clock-out', { totalHoursWorked: hours })
    //   .then(response => {
    //     console.log('Total hours worked sent to backend:', hours);
    //   })
    //   .catch(error => {
    //     console.error('Error sending total hours worked to backend:', error);
    //   });
  };

  return (
    <div className='clock-in'>
      <h1>Employee Clock In/Out</h1>
      <div>
        <label>
          Employee ID:
          <input type="text" value={employeeId} onChange={(e) => setEmployeeId(e.target.value)} />
        </label>
      </div>
      <br></br>
      <div>
        <button onClick={handleClockIn}>Clock In</button>
        <button onClick={handleClockOut}>Clock Out</button>
      </div>
      <div>
        <p>Clock In Time: {clockInTime ? clockInTime.toLocaleString() : 'N/A'}</p>
        <p>Clock Out Time: {clockOutTime ? clockOutTime.toLocaleString() : 'N/A'}</p>
        <p>Total Hours Worked: {totalHoursWorked !== null ? totalHoursWorked.toFixed(2) + ' hours' : 'N/A'}</p>
      </div>
      {message && <p>{message}</p>}
    </div>
  );
}

export default ClockIn;
