// Login.js
import React from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory hook
import './Login.css'; // Create Login.css for styling if necessary

function Login() {
  const history = useHistory(); // Get access to the history object

  const handleFormSubmit = (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    // Here you can add your authentication logic
    // For now, let's just redirect the user to the login page
    history.push('/login'); // Redirect to the login page
  };

  return (
    <div className='login-container'>
      <form className='login-form' onSubmit={handleFormSubmit}> {/* Add onSubmit event handler */}
        <h2>Login</h2>
        <div className='form-inputs'>
          <label htmlFor='username' className='form-label'>Username</label>
          <input id='username' type='text' className='form-input' placeholder='Enter your username' />
        </div>
        <div className='form-inputs'>
          <label htmlFor='password' className='form-label'>Password</label>
          <input id='password' type='password' className='form-input' placeholder='Enter your password' />
        </div>
        <button className='btn' type='submit'>Login</button>
      </form>
    </div>
  );
}

export default Login;
